/** @format */

// DOM elements
const moodForm = document.getElementById("moodForm");
const moodHistory = document.getElementById("moodHistory");
const moodSelect = document.getElementById("mood");
const moodChartElement = document.getElementById("moodChart").getContext("2d");

// Initialize mood data array
let moodData = [];

// Initialize Chart instance
let moodChart = null;

// Define mood emojis, solutions, and images
const moodDetails = {
  Happy: {
    emoji: "üòä",
    solution: "Keep spreading positivity!",
    imageUrl:
      "data:image/jpeg;base64,", // Publicly available image from Unsplash
  },
  Sad: {
    emoji: "üò¢",
    solution: "Talk to a friend or take a walk.",
    imageUrl:
      "data:image/jpeg;base64,", // Publicly available image from Unsplash
  },
  Angry: {
    emoji: "üò°",
    solution: "Try deep breathing or meditating.",
    imageUrl:
      "data:image/jpeg;base64,",
  },
  Excited: {
    emoji: "ü§©",
    solution: "Channel your excitement into creativity!",
    imageUrl:
      "data:image/jpeg;base64,", // Publicly available image from Unsplash
  },
  Neutral: {
    emoji: "üòê",
    solution: "A calm day is sometimes the best day.",
    imageUrl:
      "data:image/jpeg;base64,", // Publicly available image from Unsplash
  },
  Anxiety: {
    emoji: "üò∞",
    solution: "Take deep breaths, relax, and try grounding exercises.",
    imageUrl:
      "data:image/jpeg;base64,", // Publicly available image from Unsplash
  },
};

// Function to format date and time
function getFormattedDateTime() {
  const now = new Date();
  const date = now.toLocaleDateString();
  const time = now.toLocaleTimeString([], {
    hour: "2-digit",
    minute: "2-digit",
  });
  return `${date} at ${time}`;
}

// Function to update mood history display
function updateMoodHistory() {
  // Clear the mood history list
  moodHistory.innerHTML = "";

  // Loop through each mood entry and display it
  moodData.forEach((entry) => {
    const listItem = document.createElement("li");
    const moodInfo = moodDetails[entry.mood]; // Get emoji, solution, and image
    listItem.innerHTML = `${entry.dateTime}: ${moodInfo.emoji} <strong>${entry.mood}</strong> - ${moodInfo.solution} <img src="${moodInfo.imageUrl}" alt="${entry.mood} image">`;
    moodHistory.appendChild(listItem);
  });
}

// Function to update the mood chart
function updateMoodChart() {
  const moodCounts = moodData.reduce((acc, entry) => {
    acc[entry.mood] = (acc[entry.mood] || 0) + 1;
    return acc;
  }, {});

  // Destroy previous chart instance to avoid stacking
  if (moodChart !== null) {
    moodChart.destroy();
  }

  // Create a new chart
  moodChart = new Chart(moodChartElement, {
    type: "bar",
    data: {
      labels: Object.keys(moodCounts),
      datasets: [
        {
          label: "Mood Frequency",
          data: Object.values(moodCounts),
          backgroundColor: [
            "#4caf50",
            "#f44336",
            "#ff9800",
            "#00bcd4",
            "#9c27b0",
            "#ffb300",
          ],
          borderColor: "#333",
          borderWidth: 1,
        },
      ],
    },
    options: {
      scales: {
        y: {
          beginAtZero: true,
        },
      },
    },
  });
}

// Handle form submission
moodForm.addEventListener("submit", (e) => {
  e.preventDefault();

  // Get the selected mood
  const mood = moodSelect.value;
  const dateTime = getFormattedDateTime();

  // Add mood entry to data array
  moodData.push({ dateTime, mood });

  // Update the history and chart
  updateMoodHistory();
  updateMoodChart();

  // Reset the select field
  moodSelect.value = "";
});
